public class NegativeBinaryConverter
{
    public static int[] DecimalToNegativeBinary(int decimalNumber)
    {
        // 处理 0 的特殊情况
        if (decimalNumber == 0)
        {
            return new int[] { 0 };
        }

        // 将负数转换为其绝对值的二进制表示
        string binary = Convert.ToString(Math.Abs(decimalNumber), 2);

        // 取反
        int[] negativeBinary = new int[binary.Length];
        for (int i = 0; i < binary.Length; i++)
        {
            if (binary[i] == '0')
            {
                negativeBinary[i] = 1;
            }
            else
            {
                negativeBinary[i] = 0;
            }
        }

        // 进行借位操作
        int[] result = new int[negativeBinary.Length];
        int carry = 1;
        for (int i = negativeBinary.Length - 1; i >= 0; i--)
        {
            int sum = negativeBinary[i] + carry;
            result[i] = sum % 2;
            carry = sum / 2;
        }

        // 最终结果为负二进制表示
        return result;
    }

    public static int NegativeBinaryToDecimal(int[] negativeBinary)
    {
        // 进行权重计算
        int decimalNumber = 0;
        int power = -1;
        for (int i = negativeBinary.Length - 1; i >= 0; i--)
        {
            decimalNumber += negativeBinary[i] * (int)Math.Pow(-2, power);
            power--;
        }

        return decimalNumber;
    }
}